package com.example.demo.greetingController;
import com.example.demo.domain.album;
import com.example.demo.wep.albumRepo;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.stereotype.Controller;
import org.springframework.ui.Model;
import org.springframework.web.bind.annotation.GetMapping;
import org.springframework.web.bind.annotation.ModelAttribute;
import org.springframework.web.bind.annotation.PathVariable;
import org.springframework.web.bind.annotation.PostMapping;
import org.springframework.web.servlet.view.RedirectView;

import java.util.List;


@Controller
public class greeting {

    @Autowired
    albumRepo AlbumRepo;


        @GetMapping("/hello")
        public String helloworld(){
            System.out.println("the end point worked ");
            return "helloworld";
        }

    @GetMapping("/capitalize/{string}")
    public String stringToCapital(@PathVariable(required = false) String string, Model x){
        System.out.println("the string is: " + string);
        x.addAttribute("string", string.toUpperCase());
        return "ToCapital";
    }

    @GetMapping("/albums")
    public String getAllAlbums(Model m) {
        album FirstSong = new album("Hello", "Adel", 4, 4, "data:image/jpeg;base64,");
        album SecondSong = new album("Love the way you lie", "Eminem", 5, 5, "data:image/jpeg;base64,");
        album ThirdSong = new album("to much to ask", "Niall Horan", 6, 4, "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTVKCVcxSQOAG6JjMhgNxIhBxxVmowwGsj53g&usqp=CAU");
        album[] albums = new album[]{FirstSong, SecondSong,ThirdSong};
        m.addAttribute("albums", albums);
        return "albums";
    }

    @GetMapping("/")
    public String root(){
        System.out.println("the root page ");
        return "root";
    }
    @GetMapping("/addAlbum")
    public String AllAlbums(Model m) {
        List<album> album = (List<com.example.demo.domain.album>) AlbumRepo.findAll();
        m.addAttribute("albums", AlbumRepo.findAll());

        return "allAlbum";
    }


    @PostMapping("/addAlbum")
    public RedirectView addAlbum(String title, String artist, int songCount, int length, String imageUrl) {
        album m = new album(title, artist, songCount, length, imageUrl);
        AlbumRepo.save(m);
        return new RedirectView("/addAlbum");
    }

}
